pragma solidity ^0.4.18;

contract BountyBuster {
  struct Task {
    address poster;
    address hunter;
    uint reward;
    bytes description;
  }

  mapping(uint => Task) public tasks;
  uint public numTasks;

  constructor() public {
    numTasks = 0;
  }

  modifier incrementNumTasks() {
    _;
    numTasks += 1;
  }

  function addTask(uint _reward, bytes _description)
  public
  incrementNumTasks
  returns (uint taskId)
  {
    Task memory newTask = Task({
      poster: msg.sender,
      hunter: 0,
      reward: _reward,
      description: _description
    });
    taskId = numTasks;
    tasks[taskId] = newTask;
  }
}
